import moment from "moment";
import React from "react";
import { stagecols } from "./bands";

const DayPlan = ({ bands, site, setStep }) => {
  //Needs to order bands by time
  //Show clashes
  //Allow option to start again
  //Allow option to save as image
  const minutesOfDate = (time) => {
    const splitTime = time.split(":");
    const minutes = parseInt(splitTime[0] * 60) + parseInt(splitTime[1]);
    return minutes;
  };
  const sortedBands = bands.sort((a, b) => {
    return minutesOfDate(a.start) - minutesOfDate(b.start);
  });

  return (
    <div className="w-full">
      <h2 className="text-red-600 font-extrabold my-2 text-center">
        Here's your day plan
      </h2>
      {sortedBands.map((b, i) => {
        return (
          <div
            className={`${
              stagecols.background[b.stage]
            } rounded p-2 w-full my-4 text-center shadow-xl`}
            key={i}
          >
            <p
              className={`${
                stagecols.text[b.stage]
              } font-extrabold uppercase text-xl mb-1`}
            >
              {b.name}
            </p>
            <p className={`${stagecols.text[b.stage]} text-lg font-light`}>
              {b.stage}
            </p>
            <p
              className={`${
                stagecols.text[b.stage]
              } font-extrabold uppercase text-xl mb-1`}
            >
              {b.start} - {b.end}
              <br />
              {moment(b.start).format("hh:mm a")} -{" "}
              {moment(b.end).format("hh:mm a")}
            </p>
          </div>
        );
      })}
    </div>
  );
};
export default DayPlan;
