import React, {useState} from 'react'

const BandSelector = ({site,bands}) => {
const [chosenBands,setChosenBands] = useState()
const siteBands = bands[0][site]


    return (
        <div className="w-full">
            <h2 className="text-red-600 font-extrabold my-4 text-center">Choose which bands you want to see</h2>
            {siteBands.map((band, index) => {
                return (
                <div className={`${index %2 == 0 ? 'bg-blue-600' : 'bg-red-600' } rounded p-4 w-full my-4 mb-6 text-center shadow-xl`} key={index}>
                <p className="text-white font-extrabold uppercase text-2xl mb-1">{band.name}</p>
                <p className="text-white text-xl font-light">{band.stage}</p>
            </div>
                )
            })}


        </div>
    )
}
export default BandSelector
