import React from "react";

const DayPlan = ({ bands, site, setStep }) => {
  //Needs to order bands by time
  //Show clashes
  //Allow option to start again
  //Allow option to ave
  return (
    <div className="w-full">
      <h2 className="text-red-600 font-extrabold my-4 text-center">
        Here's your day plan
      </h2>
      <div className="bg-blue-600 rounded p-4 w-full my-4 mb-6 text-center shadow-xl">
        <p className="text-white font-extrabold uppercase text-2xl mb-1">
          Soemthing
        </p>
        <p className="text-white text-xl font-light">
          Saturday 4th September 2021
        </p>
      </div>
    </div>
  );
};
export default DayPlan;
