import React, {useState} from 'react';
import logo from './assets/logo.gif'
import SiteSelector from './SiteSelector';

const App = () => {
  const [step,setStep] = useState(0);

  return (
    <div className="h-screen flex justify-start items-center bg-white p-4 flex-col">
      <img src={logo} alt="Logo" className="w-4/5" />
      <div className="rounded bg-red-600 p-4 my-4">
        <h1 className="text-white text-xl font-extrabold tracking-wider">Slam Dunk 2021 Day Planner</h1>
        <p className="mt-4 text-white text-center">This unofficial webapp will let you choose the bands you want to see and show you a customised day plan for Slam Dunk including any clashes.</p>
      </div>
      {step == 0 &&
      <SiteSelector setStep={setStep} />
      }

  </div>
  );
}

export default App;
