import moment from "moment";
import React from "react";
import { stagecols } from "./bands";
import { toPng } from "html-to-image";

const DayPlan = ({ bands, site, setStep }) => {
  //Needs to order bands by time
  //Show clashes
  //Allow option to start again
  //Allow option to save as image
  const minutesOfDate = (time) => {
    const splitTime = time.split(":");
    const minutes = parseInt(splitTime[0] * 60) + parseInt(splitTime[1]);
    return minutes;
  };
  const sortedBands = bands.sort((a, b) => {
    return minutesOfDate(a.start) - minutesOfDate(b.start);
  });

  const onCapture = () => {
    toPng(document.getElementById("container")).then((dataUrl) =>
      download(dataUrl, "dayplan.png")
    );
  };

  return (
    <div className="w-full">
      <h2 className="text-red-600 font-extrabold my-2 text-center">
        Here's your day plan
      </h2>
      <p className="text-xs text-center">
        This has saved to when you revisit this site your plan will still be
        here. Don't forget to bookmark this site or add it to your homescreen
      </p>
      <p onClick={() => onCapture()}>click to download</p>
      {sortedBands.map((b, i) => {
        return (
          <div
            className={`${
              stagecols.background[b.stage]
            } rounded w-full my-4 text-center shadow-xl`}
            key={i}
          >
            <p
              className={`${
                stagecols.text[b.stage]
              } font-extrabold uppercase text-xl mb-1 px-2 pt-2`}
            >
              {b.name}
            </p>
            <p className={`${stagecols.text[b.stage]} text-lg font-light px-2`}>
              {b.stage}
            </p>
            <p
              className={`${
                stagecols.text[b.stage]
              } font-extrabold uppercase text-sm mb-1 px-2 pb-2`}
            >
              Starts: {b.start} (
              {moment("08/04/2021 " + b.start).format("hh:mma")}) <br />
              Ends: {b.end} ({moment("08/04/2021 " + b.end).format("hh:mma")})
            </p>
            {i < bands.length - 1 &&
              minutesOfDate(b.end) > minutesOfDate(bands[i + 1].start) && (
                <div className="w-full bg-yellow-400 text-black p-2 text-xs font-extrabold">
                  CLASH ALERT
                  <br />
                  The end of this set is after your next band starts
                </div>
              )}
          </div>
        );
      })}
    </div>
  );
};
export default DayPlan;
